member.remarks:
Sample request:

    POST /Register
    {
       "email": "admin@admin.com", - user email to register/login
       "password": "admin" - user password
    }

Sample request:

    POST /Login
    {
       "email": "admin@admin.com", - user email to register/login
       "password": "admin" - user password
    }

Sample request:

    PUT /Class/{id}
    {
       "classNumber": 1, - Number of class whose you want to add
       "profile": "PZ", - Class profile
       "description": "Sample description of class" - Class description
    }

Sample request:

    DELETE /Class/{id}
    {
       "id": 1, - ID of class whose you want to delete
    }

Sample request:

    POST /ClassManager
    {
       "UserUUID": "sample-uuid", - UUID of person whos you want to assign class
       "ClassID": 1 - ClassID of class whose you want to assign to user
    }

Sample request:

    PUT /ClassManager/{id}
    {
       "UserUUID": "sample-uuid", - UUID of person whos you want to change
       "id": 1 - ID whos you want to change
       "ClassID": 1 - New ID whos you want to assign to user
    }

Sample request:

    DELETE /ClassManager/{id}
    {
       "id": 1 - ID of record whose you want to delete
    }

Sample request:

    POST /Mark
    {
       "present": true - set person present
    }

Sample request:

    PUT /Mark
    {
       "present": true - set person present
    }

Sample request:

    PUT /Role/{id}
    {
       "firstName": "Tom", - Parent first name
       "lastName": "Jones", - Parent last name
       "email": "tom.jones@gmail.com", - Parent email
       "phone": "123456789" - Parent phone number
    }

Sample request:

    DELETE /Role/{id}
    {
       "id": 1, - id of parent whose you want to remove
    }

Sample request:

    POST /ParentManager
    {
       "UserUUID": "sample-uuid" - User uuid where you want to assign parent
       "ParentID": 3 - Parent id whos you want to assign to user
    }

Sample request:

    PUT /ParentManager/{id}
    {
       "id": 5 - id of relation person-parent whos you want to change
       "ParentID": 3 - Parent id whos you want to change
    }

Sample request:

    DELETE /ParentManager/{id}
    {
       "id": 5 - id of relation person-parent whos you want to delete
    }

Sample request:

    POST /Role
    {
       "UserUUID": "sample-uuid" - User uuid whose you want to change role
       "RoleID": 3 - Role who's you want to assign to user
    }

Sample request:

    PUT /Role/{id}
    {
       "id": 1 - Role whos you want to change
       "UserUUID": "sample-uuid" - User uuid whose you want to change role
       "NewRoleID": 3 - Role where you want to replace instead of old role
    }

Sample request:

    DELETE /Role/{id}
    {
       "id": 1 - Role whos you want to delete
       "UserUUID": "sample-uuid" - User uuid where you want to delete role
    }

Sample request:

    PUT /Teacher/{id}
    {
       "firstName": "Jon", - First name of teacher
       "lastName": "Testman", - Last name of teacher
       "gender": true, - Gernder of teacher. 1 - Male, 0 - Female
       "email": "jon.testman@gmail.com", - Teacher email
       "phone": "123456789" - Teacher phone
    }
